const o=document.getElementById("menu-button"),c=document.getElementById("header_menu");o.addEventListener("click",()=>{o.classList.toggle("button-open"),c.classList.toggle("active");var e=document.getElementById("url").value,t=e+"/assets/icons/icon-park-outline_down.svg",s=e+"/assets/icons/down.png",n=document.getElementById("arrow");n.src=n.src===t?s:t});const l=document.getElementById("plus-button"),d=document.getElementById("despliegue-box");l.addEventListener("click",()=>{d.classList.toggle("active")});const u=document.getElementById("flechaTop");u.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})});
